function adjustVideoPositions(){document.querySelectorAll(".video-background").forEach((e=>{const o=e.getBoundingClientRect(),t=o.left+window.pageXOffset,i=o.top+window.pageYOffset,d=e.querySelector(".myVideo");d&&(d.style.transform=`translate(${-t}px, ${-i}px)`)}))}window.addEventListener("load",adjustVideoPositions),window.addEventListener("resize",adjustVideoPositions),window.addEventListener("scroll",adjustVideoPositions);